class Footer extends React.Component {
    state = {
      isHovered: false,
      isTyping: false,
    }

    handleLinkHover = () => {
      this.setState({
        isHovered: true,
        isTyping: true,
      });
    }

    handleLinkMouseOut = () => {
      this.setState({
        isHovered: false,
        isTyping: false,
      });
    }

    render() {
      const { isHovered, isTyping } = this.state;

      return (
        <div className='Footer'>
          <div className='FooterSubscribe'>
            <iframe src="https://icantcope.substack.com/embed" width="480" height="100" frameborder="0"></iframe>
            {/* <div className='OverlayButton'>
              <p className='OverlayButtonCopy'>Send it</p>
            </div> */}
            <p className='FooterSubscribeDescription'>Reflections in your inbox. No spam – that would be <i>against</i> the point.</p>
          </div>
          <a href="https://twitter.com/gvns0x" rel="noreferrer" target="_blank" onMouseOver={this.handleLinkHover} onMouseOut={this.handleLinkMouseOut}>gvns↗</a>
          {isHovered && <p className={`Warning ${isTyping ? 'isTyping' : ''}`}>This will take you to my profile on Twitter. Be intentional. Don't get lost.</p>}
        </div>
      );
    }
  }
