import React from 'react';
import '../Footer/Footer.css';

class Footer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isHovering: false
        };
    }

    handleMouseEnter = () => {
        this.setState({ isHovering: true });
    }

    handleMouseLeave = () => {
        this.setState({ isHovering: false });
    }

    render() {
        return <div className='Footer'>
            <div className='FooterSubscribe'>
                <iframe src="https://icantcope.substack.com/embed" width="480" height="100" frameborder="0" ></iframe>
                {/* <div className='OverlayButton'>
                <p className='OverlayButtonCopy'>Send it</p>
            </div> */}
                <p className='FooterSubscribeDescription'>Reflections in your inbox. No spam – that would be <i>against</i> the point.</p>
            </div>
            <a href="https://twitter.com/gvns0x" rel="noreferrer" target="_blank" onMouseEnter={this.handleMouseEnter} onMouseLeave={this.handleMouseLeave}>gvns↗</a>
            {this.state.isHovering && <p className='Warning'>This will take you to my profile on Twitter. Be intentional. Don't get lost.</p>}
        </div>
    }
}

export default Footer;
