import React from 'react';
import '../Footer/Footer.css';

class Footer extends React.Component {
    state = {
        isHovered: false,
        mouseX: 0,
        mouseY: 0
    }

    handleLinkHover = (e) => {
        this.setState({
            isHovered: true,
            mouseX: e.clientX,
            mouseY: e.clientY
        });
    }

    handleLinkMouseOut = () => {
        this.setState({
            isHovered: false
        });
    }

    render() {
        const { isHovered, mouseX, mouseY } = this.state;

        const warningStyle = {
            transform: isHovered ? `translate3d(${mouseX}px, ${mouseY}px, 0)` : 'none',
            opacity: isHovered ? 1 : 0
        };

        return (
            <div className='Footer'>
                <div className='FooterSubscribe'>
                    <iframe src="https://icantcope.substack.com/embed" width="480" height="100" frameborder="0"></iframe>
                    {/* <div className='OverlayButton'>
                    <p className='OverlayButtonCopy'>Send it</p>
                </div> */}
                    <p className='FooterSubscribeDescription'>Reflections in your inbox. No spam – that would be <i>against</i> the point.</p>
                </div>
                <a href="https://twitter.com/gvns0x" rel="noreferrer" target="_blank" onMouseOver={this.handleLinkHover} onMouseOut={this.handleLinkMouseOut}>gvns↗</a>
                <p className='Warning' style={warningStyle}>This will take you to my profile on Twitter. Be intentional. Don't get lost.</p>
            </div>
        );
    }
}

export default Footer;
