import React from 'react';
import '../Footer/Footer.css';

class Footer extends React.Component {
    constructor(props) {
        super(props);

        this.warningRef = React.createRef();
    }

    state = {
        isHovered: false,
        warningTransformX: 0,
        warningTransformY: 0
    }

    handleLinkHover = (event) => {
        const link = event.currentTarget;
        const linkRect = link.getBoundingClientRect();
        const mouseX = event.clientX - linkRect.left;
        const mouseY = event.clientY - linkRect.top;
        const maxOffset = 10;

        const offsetX = (mouseX / linkRect.width) * maxOffset - maxOffset / 2;
        const offsetY = (mouseY / linkRect.height) * maxOffset - maxOffset / 2;

        this.setState({
            isHovered: true,
            warningTransformX: offsetX,
            warningTransformY: offsetY
        });
    }

    handleLinkMouseOut = () => {
        this.setState({
            isHovered: false
        });
    }

    render() {
        const { isHovered, warningTransformX, warningTransformY } = this.state;

        return (
            <div className='Footer'>
                <div className='FooterSubscribe'>
                    <iframe src="https://icantcope.substack.com/embed" width="480" height="100" frameborder="0"></iframe>
                    {/* <div className='OverlayButton'>
                    <p className='OverlayButtonCopy'>Send it</p>
                </div> */}
                    <p className='FooterSubscribeDescription'>Reflections in your inbox. No spam – that would be <i>against</i> the point.</p>
                </div>
                <a href="https://twitter.com/gvns0x" rel="noreferrer" target="_blank" onMouseOver={this.handleLinkHover} onMouseOut={this.handleLinkMouseOut}>gvns↗</a>
                {isHovered && <p ref={this.warningRef} className='Warning' style={{ transform: `translate3d(${warningTransformX}px, ${warningTransformY}px, 0)` }}>This will take you to my profile on Twitter. Be intentional. Don't get lost.</p>}
            </div>
        );
    }
}

export default Footer;
